-- 0003_arp_seed_data.sql
-- One-time seed of ARP activities + sources into Postgres.

CREATE SCHEMA IF NOT EXISTS "__ARP_SCHEMA__";

-- Activities
ON CONFLICT (activity_id) DO UPDATE SET
  activity_slug=EXCLUDED.activity_slug,
  activity_name=EXCLUDED.activity_name,
  activity_category=EXCLUDED.activity_category,
  scope_notes=EXCLUDED.scope_notes,
  status=EXCLUDED.status,
  updated_at=now();

-- Sources
ON CONFLICT (source_id) DO UPDATE SET
  activity_id=EXCLUDED.activity_id,
  activity_name=EXCLUDED.activity_name,
  title=EXCLUDED.title,
  organization=EXCLUDED.organization,
  jurisdiction=EXCLUDED.jurisdiction,
  url=EXCLUDED.url,
  source_type=EXCLUDED.source_type,
  activities_covered_raw=EXCLUDED.activities_covered_raw,
  brief_focus=EXCLUDED.brief_focus,
  authority_class=EXCLUDED.authority_class,
  publication_date=EXCLUDED.publication_date; 

-- Ensure documents rows exist
SELECT s.source_id FROM "__ARP_SCHEMA__".sources s
ON CONFLICT (source_id) DO NOTHING;

